// start owl owlCarousel
$(document).ready(function () {
    $("#banner .movies").owlCarousel({
        loop: true,
        nav: false,
        items: 1,
        dots: false,
        autoplay: true
    });

    // listing movies
    $(".list .movies").owlCarousel({
        loop: true,
        //nav: false,
        responsiveClass: true,
        margin: 8,
        responsive: {
            0: {
                items: 2,
                nav: true
            },
            500: {
                items: 3,
                nav: false
            },
            770: {
                items: 4,
                nav: true,
                stagePadding: 15,
            },
            1200: {
                items: 5,
                nav: true,
                loop: true,
                stagePadding: 40,
            }
        },
        dots: false,
        autoplay: true,
        autoplayTimeout: 3000,
        autoplayHoverPause: true
    });

    $(window).on('scroll', function () {
        $(this).scrollTop() > 100 ? $('.navbar').addClass('bg-transparent-nav') : $('.navbar').removeClass('bg-transparent-nav')
    });
}); //-- end document